import { useState } from 'react';
import './App.css';

function App() {

const [city,setcity] = useState('');

const[weatherInfo,setweatherInfo] = useState(null);

function getweather() {

  const apiKey = '8c21c6d3ffb0d2fc5cb428690c577fe2';
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;


  fetch(url)
   .then((response) => response.json())
   .then((data) => {
    // console.log(data);
    let MT = Math.round(data.main.temp);
    let FL = Math.round(data.main.feels_like);

    const weather = {
      location:'weather in ${data.name}',
      temp:'temperature ${MT}',
      feels_like:'feels like ${FL}',
      humidity:'humidity ${data.main.humidity}',
      wind:'wind ${data.wind.speed}',
      condition:''

    }


   });





}

  return (
    <div className="App">
     <input 
     type = 'text'
     placeholder='Enter the city'
     value={city}
     onChange={(e)=>setcity(e.target.value)}
     />
     <button onClick={getweather}>Get Weather</button>
     {weatherInfo && (<div className='weather-info'>
      <h3></h3>
      <p></p>
      <p></p>
      <p></p>
      <p></p>
      <p></p>
     </div>)}


    </div>
  );
}

export default App;
